casper.start('http://test.oae.com/', function(){
    // Create an user to test with
    var user1 = null;
    userUtil().createUsers(1, function(users) {
        user1 = users[0];
    });
    
    // Log in with that user
    casper.then(function() {
        userUtil().doLogIn(user1.username, 'password');
    });
    
    var fileUrl = null;
    casper.then(function() {
        contentUtil().createFile(function(url) {
            fileUrl = url;
        });
    });
    
    casper.thenOpen('http://test.oae.com/me', function() {
        verifyOpenContent();
        verifyEditContent();
        verifyEditContentForm();
        verifyEditContentFormValidation();
    });
    
    casper.then(function() {
        userUtil().doLogOut();
    });
});

